('questions/getQuestionById')?>",            dataType:"json",            data: {                quesId:quesId            },            success:function (msg) {                console.log(msg);                var ques_item="";                if(msg.quesitem.question.length>0){                    $(msg.quesitem.question).each(function(index,element){                        var ques_item_option = ""                        if(msg.quesitem.question[index].ques_item.length > 0){                            $(msg.quesitem.question[index].ques_item).each(function(i,ele){                                ques_item_option +=                                    "<li class='clearfix'>"                                    +"<div class='per-5 pull-left text-center'><input type='checkbox'></div>"                                    +"<div class='per-95 pull-left text-justify'>"+ele.title+"</div>"                                    +"</li>"                            });                        }                        ques_item +=                            "<li class='pagb-15 mgb-10'>"                            +"<div class='clearfix pagh-5 pagb-15'>"                            +"<div class='col-sm-1'>问题:"+index+"</div>"                            +"<div class='col-sm-11'>"+element.title+"</div>"                            +"</div>"                            +"<ul>"                            + ques_item_option                            +"</ul>"                            +"</li>";                    });                }                var yltitle='';                if(msg.data[0].skill_id==1){                    //阅读理解                    yltitle="<div style='margin-bottom:30px;'>"                        +"<div class='clearfix listen-title' style='height: 200px;overflow: hidden;'>"                        + "<span>"+msg.data[0].content+"</span>"                        +"<div class='audition-btn pull-left'>"                        +"</div>"                        +"</div>"                        +"</div>";                }else if(msg.data[0].skill_id==2){                    //段落匹配                    yltitle="<div class='clearfix mgb-10'><div class='pull-left per-5 text-center'>"+msg.data[0].content+"</div><div class='pull-left per-95 text-justify'>"+msg.data[0].content+"</div></div>"                }else if(msg.data[0].skill_id==3){                    //翻译                    yltitle="<div class='pag-15'> <p class='mgb-10'>"+msg.data[0].content+"</p></div>"                }else if(msg.data[0].skill_id==4||msg.data[0].skill_id==5){                    //听音频                    yltitle="<div style='margin-bottom:30px;'>"                        +"<div class='clearfix listen-title'>"                        +"<span>"+msg.data[0].content+"</span>"                        +"<span style='color:#ededed;'>"+msg.data[0].sub_section+"</span>"                        +"<div class='audition-btn pull-left'>"                        +"<button class='btn btn-success audio_listen_yl' data-time='"+msg.data[0].sub_section+"' data-audio='http://112.124.2.173/weeduTalk/media/audio/"+msg.data[0].content+"'>试听</button>"                        +"<audio src='' controls='' class='audition-target'></audio>"                        +"</div>"                        +"</div>"                        +"</div>";                }else if(msg.data[0].skill_id==6){                    //写作                    yltitle="<div class='pag-15'> <p class='mgb-10'>"+msg.data[0].content+"</p></div>"                }else if(msg.data[0].skill_id==7){                    //选词填空                    yltitle="<div class='pag-15 bg-f8fdf8 banked-selected'><p>"+msg.data[0].content+"</p></div>"                }                var ques_detail=                    "<div>"                    +"<div class='exam-detail-tit clearfix'>"                    +"<div class='exam-tit-table'>"                    +"<table>"                    +"<thead>"                    +"<tr><td>题目编号</td><td>考查技能</td><td>答题类型</td><td>内容类型</td><td>题目难度</td><td>创建时间</td>"                    +"<td>创建人</td><td>主题</td><td>所属学校</td><td>正确率</td><td>使用次数</td></tr>"                    +"</thead>"                    +"<tbody>"                    +"<tr><td>"+msg.data[0].subject_num+"</td><td>"+msg.data[0].exa_skill+"</td><td>"+msg.data[0].answer_type+"</td><td>"+msg.data[0].content_type+"</td><td>"+msg.data[0].subject_diff+"</td><td>"+msg.data[0].build_time+"</td><td>"+msg.data[0].build_user+"</td>"                    +"<td>"+msg.data[0].theme+"</td><td>"+msg.data[0].school+"</td><td>"+msg.data[0].accuracy+"</td><td>"+msg.data[0].use_count+"</td></tr>"                    +"</tbody>"                    +"</table>"                    +"</div></div>"                    + yltitle                    +"<div class='pag-15 exam-detail-cont'>"                    +"<div class='pag-15'>"                    +"<ul>"                    + ques_item                    +"</ul>"                    +"</div>"                    +"</div>"                    +"<div class='bg-ed exam-detail-notes pag-15'>"                    +"<div>建议考试时间 <i>"+msg.data[0].answer_time+"分钟</i></div>"                    +"<div>建议每题分数 <i>"+msg.data[0].sub_score+"分</i></div>"//                    +"<div>考题数量 <i>"+msg.data[0].sub_score+"</i></div>"                    +"</div>"                    +"</div>";                questionDeatil.empty();                questionDeatil.append(ques_detail);            }        });    }        function wordStatic(input) {        // 获取文本框对象        var el = $(".dcs");        if (el && input) {            var value = input.value;            ;            // 更新计数            el.text(value.split(" ").length-1);        }    }    function JqValidate(ele)    {        if(ele.find('input[type="text"]').length>1){            var text=true,check=false;            ele.find('input[type="text"]').each(function () {                if($(this).val() == ''){                    text=false;                    return false;                }            });            ele.find('input[type="checkbox"]').each(function () {                if($(this).val() == 1){                    check=true;                    return false;                }            });        }else{            text=true;            check = true;        }        if(ele.find('textarea').length>0){            var statue=true;            ele.find('textarea').each(function () {              if($(this).val() == ''){                  statue=false;                  return false;              }            });        }else{            statue=true;        }        var statue_num=true;        ele.find('input[type="number"]').each(function () {            if($(this).val() == ''){                statue_num = false;                return false;            }        });        if(statue_num==true&&statue==true&&text==true&&check==true){            return true;        }else{            return false;        }    }    </script></body></html>